import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

// =======================
// SHARED COMPONENTS
// =======================

const Layout = ({ children, activeTab, setActiveTab }) => {
  const tabs = [
    { id: 'standup', name: 'Standup', icon: 'ðŸ“' },
    { id: 'ticket', name: 'Tickets', icon: 'ðŸŽ«' },
    { id: 'sprint', name: 'Sprint', icon: 'âš¡' },
    { id: 'blockers', name: 'Blockers', icon: 'ðŸš«' },
    { id: 'retro', name: 'Retrospective', icon: 'ðŸ”„' },
    { id: 'dashboard', name: 'Dashboard', icon: 'ðŸ“Š' }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-sm">AI</span>
              </div>
              <h1 className="text-xl font-semibold text-gray-900">Scrum Master</h1>
            </div>
            <div className="text-sm text-gray-500">
              AI-powered Agile Management Demo
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors duration-200 ${
                  activeTab === tab.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <span className="mr-2">{tab.icon}</span>
                {tab.name}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {children}
      </main>
    </div>
  );
};

const ExportButton = ({ data, filename, format = 'json' }) => {
  const handleExport = () => {
    let content;
    let mimeType;

    if (format === 'json') {
      content = JSON.stringify(data, null, 2);
      mimeType = 'application/json';
    } else if (format === 'markdown') {
      content = data.markdown_format || data.formatted_output || JSON.stringify(data, null, 2);
      mimeType = 'text/markdown';
    } else if (format === 'slack') {
      content = data.slack_format || data.formatted_output || JSON.stringify(data, null, 2);
      mimeType = 'text/plain';
    }

    const blob = new Blob([content], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${filename}.${format === 'json' ? 'json' : format === 'markdown' ? 'md' : 'txt'}`;
    document.body.appendChild(a);
    a.click();
    URL.revokeObjectURL(url);
    document.body.removeChild(a);
  };

  return (
    <button
      onClick={handleExport}
      className="inline-flex items-center px-3 py-1.5 border border-gray-300 shadow-sm text-xs font-medium rounded text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
    >
      <span className="mr-1">ðŸ“¥</span>
      {format.toUpperCase()}
    </button>
  );
};

// =======================
// MODULE COMPONENTS
// =======================

const StandupModule = () => {
  const [input, setInput] = useState({
    yesterday: '',
    today: '',
    blockers: ''
  });
  const [output, setOutput] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recentStandups, setRecentStandups] = useState([]);

  useEffect(() => {
    fetchRecentStandups();
  }, []);

  const fetchRecentStandups = async () => {
    try {
      const response = await axios.get(`${API}/standup?limit=5`);
      setRecentStandups(response.data);
    } catch (error) {
      console.error('Error fetching standups:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post(`${API}/standup`, input);
      setOutput(response.data);
      fetchRecentStandups();
    } catch (error) {
      console.error('Error generating standup:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadExample = () => {
    setInput({
      yesterday: "Completed user authentication module and fixed 3 critical bugs in the payment system",
      today: "Working on payment integration, user dashboard improvements, and sprint planning",
      blockers: "Waiting for API keys from payment provider, potential delay of 1-2 days"
    });
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
      {/* Input Panel */}
      <div className="space-y-6">
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-medium text-gray-900">Daily Standup Input</h2>
            <button
              onClick={loadExample}
              className="text-sm text-blue-600 hover:text-blue-800 underline"
            >
              Load Example
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                What did you work on yesterday?
              </label>
              <textarea
                value={input.yesterday}
                onChange={(e) => setInput({...input, yesterday: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="Describe your completed tasks, achievements, and progress..."
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                What will you work on today?
              </label>
              <textarea
                value={input.today}
                onChange={(e) => setInput({...input, today: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="List your planned tasks and objectives for today..."
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Any blockers or challenges?
              </label>
              <textarea
                value={input.blockers}
                onChange={(e) => setInput({...input, blockers: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="2"
                placeholder="Describe any obstacles, dependencies, or support needed..."
              />
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? 'Generating...' : 'Generate Standup Summary'}
            </button>
          </form>
        </div>

        {/* Recent Standups */}
        {recentStandups.length > 0 && (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-md font-medium text-gray-900 mb-4">Recent Standups</h3>
            <div className="space-y-2">
              {recentStandups.map((standup) => (
                <div key={standup.id} className="p-3 bg-gray-50 rounded border text-sm">
                  <div className="text-gray-600 text-xs mb-1">
                    {new Date(standup.timestamp).toLocaleDateString()}
                  </div>
                  <div className="text-gray-800">{standup.summary}</div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Output Panel */}
      <div className="space-y-6">
        {output ? (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-medium text-gray-900">AI Generated Summary</h2>
              <div className="flex space-x-2">
                <ExportButton data={output} filename="standup-summary" format="markdown" />
                <ExportButton data={output} filename="standup-summary" format="slack" />
                <ExportButton data={output} filename="standup-summary" format="json" />
              </div>
            </div>

            <div className="prose max-w-none">
              <pre className="whitespace-pre-wrap text-sm bg-gray-50 p-4 rounded border font-sans">
                {output.formatted_output}
              </pre>
            </div>
          </div>
        ) : (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200 border-dashed">
            <div className="text-center text-gray-500">
              <div className="text-4xl mb-4">ðŸ“</div>
              <h3 className="text-lg font-medium mb-2">Generate Your First Standup</h3>
              <p className="text-sm">
                Fill out the form on the left to generate a professional standup summary.
                The AI will create formatted output suitable for Slack, Jira, or documentation.
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const TicketModule = () => {
  const [input, setInput] = useState({
    description: '',
    project_context: ''
  });
  const [output, setOutput] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recentTickets, setRecentTickets] = useState([]);

  useEffect(() => {
    fetchRecentTickets();
  }, []);

  const fetchRecentTickets = async () => {
    try {
      const response = await axios.get(`${API}/ticket?limit=5`);
      setRecentTickets(response.data);
    } catch (error) {
      console.error('Error fetching tickets:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post(`${API}/ticket`, input);
      setOutput(response.data);
      fetchRecentTickets();
    } catch (error) {
      console.error('Error generating ticket:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadExample = () => {
    setInput({
      description: "Need to implement a user dashboard that shows real-time analytics, recent activity, and allows users to export their data. Should include responsive design and dark mode support.",
      project_context: "SaaS platform with React frontend and FastAPI backend"
    });
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
      {/* Input Panel */}
      <div className="space-y-6">
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-medium text-gray-900">Ticket Generator</h2>
            <button
              onClick={loadExample}
              className="text-sm text-blue-600 hover:text-blue-800 underline"
            >
              Load Example
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Feature/Bug Description *
              </label>
              <textarea
                value={input.description}
                onChange={(e) => setInput({...input, description: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="4"
                placeholder="Describe the feature request, bug report, or task in natural language..."
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Project Context (Optional)
              </label>
              <textarea
                value={input.project_context}
                onChange={(e) => setInput({...input, project_context: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="2"
                placeholder="Additional context about the project, technology stack, or requirements..."
              />
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? 'Generating...' : 'Generate Structured Ticket'}
            </button>
          </form>
        </div>

        {/* Recent Tickets */}
        {recentTickets.length > 0 && (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-md font-medium text-gray-900 mb-4">Recent Tickets</h3>
            <div className="space-y-2">
              {recentTickets.map((ticket) => (
                <div key={ticket.id} className="p-3 bg-gray-50 rounded border text-sm">
                  <div className="flex justify-between items-start mb-1">
                    <div className="font-medium text-gray-800">{ticket.title}</div>
                    <span className={`px-2 py-1 text-xs rounded ${
                      ticket.priority === 'High' ? 'bg-red-100 text-red-700' :
                      ticket.priority === 'Medium' ? 'bg-yellow-100 text-yellow-700' :
                      'bg-green-100 text-green-700'
                    }`}>
                      {ticket.priority}
                    </span>
                  </div>
                  <div className="text-gray-600 text-xs">
                    {ticket.estimated_hours}h â€¢ {ticket.story_points}pt â€¢ {new Date(ticket.timestamp).toLocaleDateString()}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Output Panel */}
      <div className="space-y-6">
        {output ? (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-medium text-gray-900">Generated Ticket</h2>
              <div className="flex space-x-2">
                <ExportButton data={output} filename="ticket" format="json" />
              </div>
            </div>

            <div className="space-y-4">
              <div>
                <h3 className="font-medium text-gray-900 mb-2">{output.title}</h3>
                <div className="flex space-x-4 text-sm text-gray-600 mb-4">
                  <span className={`px-2 py-1 rounded ${
                    output.priority === 'High' ? 'bg-red-100 text-red-700' :
                    output.priority === 'Medium' ? 'bg-yellow-100 text-yellow-700' :
                    'bg-green-100 text-green-700'
                  }`}>
                    {output.priority}
                  </span>
                  <span className="bg-blue-100 text-blue-700 px-2 py-1 rounded">
                    {output.estimated_hours}h
                  </span>
                  <span className="bg-purple-100 text-purple-700 px-2 py-1 rounded">
                    {output.story_points} points
                  </span>
                </div>
              </div>

              <div>
                <h4 className="font-medium text-gray-900 mb-2">Description</h4>
                <div className="text-sm text-gray-700 bg-gray-50 p-3 rounded">
                  <pre className="whitespace-pre-wrap font-sans">{output.description}</pre>
                </div>
              </div>

              <div>
                <h4 className="font-medium text-gray-900 mb-2">Acceptance Criteria</h4>
                <ul className="text-sm text-gray-700 space-y-1">
                  {output.acceptance_criteria.map((criteria, index) => (
                    <li key={index} className="flex items-start">
                      <span className="text-green-500 mr-2">âœ“</span>
                      {criteria}
                    </li>
                  ))}
                </ul>
              </div>

              <div>
                <h4 className="font-medium text-gray-900 mb-2">Labels</h4>
