function App() {
  const [activeTab, setActiveTab] = useState('dashboard');

  const renderActiveModule = () => {
    switch (activeTab) {
      case 'standup':
        return <StandupModule />;
      case 'ticket':
        return <TicketModule />;
      case 'sprint':
        return <SprintModule />;
      case 'blockers':
        return <BlockersModule />;
      case 'retro':
        return <RetroModule />;
      case 'dashboard':
        return <Dashboard />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="App">
      <Layout activeTab={activeTab} setActiveTab={setActiveTab}>
        {renderActiveModule()}
      </Layout>
    </div>
  );
}

export default App;
