import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

// =======================
// SHARED COMPONENTS
// =======================

const Sidebar = ({ activeTab, setActiveTab }) => {
  const menuItems = [
    { id: 'dashboard', name: 'Dashboard', icon: 'ðŸ“Š', desc: 'Overview & Analytics' },
    { id: 'standup', name: 'Daily Standup', icon: 'ðŸ‘¥', desc: 'AI Update Composer' },
    { id: 'sprint', name: 'Sprint Planning', icon: 'âš¡', desc: 'AI Story Splitter' },
    { id: 'ticket', name: 'Task Generator', icon: 'ðŸ“‹', desc: 'Smart Ticket Builder' },
    { id: 'retro', name: 'Retrospective', icon: 'ðŸ”„', desc: 'Sprint Reflection Tool' },
    { id: 'blockers', name: 'Backlog Groomer', icon: 'ðŸš§', desc: 'Prioritization Assistant' },
    { id: 'insights', name: 'AI Insights', icon: 'ðŸ§ ', desc: 'Predictive Analytics' },
    { id: 'integrations', name: 'Integrations', icon: 'ðŸ”—', desc: 'JIRA â€¢ Slack â€¢ GitHub' }
  ];

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <div className="logo">
          <div className="logo-icon">AI</div>
          <div className="logo-text">
            <div className="logo-title">AI Scrum Master</div>
            <div className="logo-subtitle">Intelligent Agile Automation</div>
          </div>
        </div>
      </div>

      <nav className="sidebar-nav">
        {menuItems.map((item) => (
          <button
            key={item.id}
            onClick={() => setActiveTab(item.id)}
            className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
          >
            <div className="nav-icon">{item.icon}</div>
            <div className="nav-content">
              <div className="nav-title">{item.name}</div>
              <div className="nav-desc">{item.desc}</div>
            </div>
          </button>
        ))}
      </nav>

      <div className="sidebar-footer">
        <div className="footer-badge">
          <span className="badge-icon">ðŸ¤–</span>
          <span className="badge-text">Enterprise AI-Powered</span>
        </div>
      </div>
    </div>
  );
};

const TopBar = ({ title, subtitle }) => {
  const [notifications, setNotifications] = useState([]);

  return (
    <div className="topbar">
      <div className="topbar-content">
        <div className="topbar-info">
          <h1 className="topbar-title">{title}</h1>
          <p className="topbar-subtitle">{subtitle}</p>
        </div>
        <div className="topbar-actions">
          <div className="notification-badge">
            <span className="notification-icon">ðŸ””</span>
            <span className="notification-count">3</span>
          </div>
          <div className="status-indicator">
            <div className="status-dot active"></div>
            <span className="status-text">All Systems Operational</span>
          </div>
        </div>
      </div>
    </div>
  );
};

const ExportButton = ({ data, filename, format = 'json', size = 'normal' }) => {
  const handleExport = async () => {
    try {
      if (data.id && format !== 'json') {
        // Use backend export endpoint for formatted exports
        const module = filename.split('-')[0]; // Extract module from filename
        const response = await axios.get(`${API}/export/${module}/${data.id}?format=${format}`);

        const blob = new Blob([response.data.content], { 
          type: response.data.content_type || 'text/plain' 
        });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${filename}.${format === 'markdown' ? 'md' : format}`;
        document.body.appendChild(a);
        a.click();
        URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        // Local export for JSON
        const content = JSON.stringify(data, null, 2);
        const blob = new Blob([content], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${filename}.json`;
        document.body.appendChild(a);
        a.click();
        URL.revokeObjectURL(url);
        document.body.removeChild(a);
      }
    } catch (error) {
      console.error('Export failed:', error);
    }
  };

  const formatColors = {
    json: 'bg-blue-100 text-blue-700 hover:bg-blue-200',
    markdown: 'bg-green-100 text-green-700 hover:bg-green-200',
    slack: 'bg-purple-100 text-purple-700 hover:bg-purple-200',
    jira: 'bg-orange-100 text-orange-700 hover:bg-orange-200'
  };

  return (
    <button
      onClick={handleExport}
      className={`export-btn ${formatColors[format]} ${size === 'small' ? 'small' : ''}`}
    >
      <span className="export-icon">ðŸ“¥</span>
      <span>{format.toUpperCase()}</span>
    </button>
  );
};

const LoadingSpinner = ({ text = "Loading..." }) => (
  <div className="loading-spinner">
    <div className="spinner"></div>
    <span>{text}</span>
  </div>
);

const MetricCard = ({ title, value, change, icon, color, trend }) => (
  <div className={`metric-card ${color}`}>
    <div className="metric-header">
      <div className="metric-icon">{icon}</div>
      <div className="metric-trend">
        {trend && <span className={`trend-indicator ${trend.direction}`}>{trend.arrow}</span>}
      </div>
    </div>
    <div className="metric-content">
      <div className="metric-value">{value}</div>
      <div className="metric-title">{title}</div>
      <div className="metric-change">{change}</div>
    </div>
  </div>
);

// =======================
// DASHBOARD COMPONENT
// =======================

const Dashboard = () => {
  const [metrics, setMetrics] = useState(null);
  const [insights, setInsights] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
    const interval = setInterval(fetchDashboardData, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [metricsResponse, insightsResponse] = await Promise.all([
        axios.get(`${API}/metrics`),
        axios.get(`${API}/insights?limit=5`)
      ]);
      setMetrics(metricsResponse.data);
      setInsights(insightsResponse.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <LoadingSpinner text="Loading dashboard analytics..." />
      </div>
    );
  }

  const metricCards = [
    {
      title: 'Team Velocity',
      value: `${metrics?.avg_team_velocity || 0}`,
      change: '+15% from last sprint',
      icon: 'âš¡',
      color: 'blue',
      trend: { direction: 'up', arrow: 'â†—ï¸' }
    },
    {
      title: 'Team Health Score',
      value: `${metrics?.team_health_score || 0}/100`,
      change: 'Excellent team morale',
      icon: 'â¤ï¸',
      color: 'green',
      trend: { direction: 'up', arrow: 'â†—ï¸' }
    },
    {
      title: 'Sprint Progress',
      value: `${metrics?.burndown_data?.current_sprint?.completed_points || 0}/${metrics?.burndown_data?.current_sprint?.total_points || 0}`,
      change: 'On track for completion',
      icon: 'ðŸŽ¯',
      color: 'purple',
      trend: { direction: 'stable', arrow: 'â†’' }
    },
    {
      title: 'Active Blockers',
      value: metrics?.blockers_detected || 0,
      change: '3 auto-resolved today',
      icon: 'ðŸš«',
      color: 'red',
      trend: { direction: 'down', arrow: 'â†˜ï¸' }
    },
    {
      title: 'Delivery Predictability',
      value: `${metrics?.productivity_trends?.delivery_predictability || 0}%`,
      change: 'Highly predictable',
      icon: 'ðŸ“ˆ',
      color: 'indigo',
      trend: { direction: 'up', arrow: 'â†—ï¸' }
    },
    {
      title: 'Code Quality',
      value: `${metrics?.productivity_trends?.code_quality_score || 0}%`,
      change: '+5% improvement',
      icon: 'â­',
      color: 'yellow',
      trend: { direction: 'up', arrow: 'â†—ï¸' }
    }
  ];

  return (
    <div className="dashboard">
      <TopBar 
        title="AI Scrum Master Dashboard" 
        subtitle="Your intelligent Agile automation overview - Real-time insights and predictive analytics"
      />

      <div className="dashboard-content">
        {/* Enhanced Metrics Grid */}
        <div className="metrics-grid">
          {metricCards.map((card, index) => (
            <MetricCard key={index} {...card} />
          ))}
        </div>

        {/* Main Dashboard Grid */}
        <div className="dashboard-main-grid">
          {/* Sprint Overview */}
          <div className="dashboard-card sprint-overview">
            <div className="card-header">
              <div className="card-title">
                <span className="card-icon">ðŸŽ¯</span>
                <h3>Current Sprint - Sprint 24</h3>
              </div>
              <div className="status-badge active">Active</div>
            </div>

            <div className="sprint-details">
              <p className="sprint-goal">Complete user authentication system and payment bug fixes</p>

              <div className="progress-section">
                <div className="progress-header">
                  <span>Sprint Progress</span>
                  <span className="progress-value">
                    {Math.round((metrics?.burndown_data?.current_sprint?.completed_points || 0) / 
                    (metrics?.burndown_data?.current_sprint?.total_points || 1) * 100)}%
                  </span>
                </div>
                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{
                      width: `${Math.round((metrics?.burndown_data?.current_sprint?.completed_points || 0) / 
                      (metrics?.burndown_data?.current_sprint?.total_points || 1) * 100)}%`
                    }}
                  ></div>
                </div>
                <div className="progress-details">
                  <span>{metrics?.burndown_data?.current_sprint?.completed_points || 0} of {metrics?.burndown_data?.current_sprint?.total_points || 0} story points completed</span>
                  <span>{metrics?.burndown_data?.current_sprint?.days_remaining || 0} days remaining</span>
                </div>
              </div>

              <div className="task-distribution">
                <div className="distribution-item completed">
                  <div className="distribution-count">12</div>
                  <div className="distribution-label">Completed</div>
                </div>
                <div className="distribution-item progress">
                  <div className="distribution-count">5</div>
                  <div className="distribution-label">In Progress</div>
                </div>
                <div className="distribution-item blocked">
                  <div className="distribution-count">2</div>
                  <div className="distribution-label">Blocked</div>
                </div>
                <div className="distribution-item todo">
                  <div className="distribution-count">8</div>
                  <div className="distribution-label">To Do</div>
                </div>
              </div>
            </div>
          </div>

          {/* AI Insights Panel */}
          <div className="dashboard-card insights-panel">
            <div className="card-header">
              <div className="card-title">
                <span className="card-icon">ðŸ§ </span>
                <h3>AI Insights & Predictions</h3>
              </div>
              <button className="view-all-btn">View All</button>
            </div>

            <div className="insights-list">
              {insights.map((insight, index) => (
                <div key={index} className={`insight-item ${insight.impact}`}>
                  <div className="insight-header">
                    <div className="insight-type">{insight.type}</div>
                    <div className="insight-confidence">{Math.round(insight.confidence * 100)}% confidence</div>
                  </div>
                  <h4 className="insight-title">{insight.title}</h4>
                  <p className="insight-description">{insight.description}</p>
                  <div className="insight-actions">
                    {insight.suggested_actions.slice(0, 2).map((action, actionIndex) => (
                      <span key={actionIndex} className="action-tag">{action}</span>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Team Performance */}
          <div className="dashboard-card team-performance">
            <div className="card-header">
              <div className="card-title">
                <span className="card-icon">ðŸ‘¥</span>
                <h3>Team Performance</h3>
              </div>
            </div>

            <div className="performance-metrics">
              <div className="performance-item">
                <div className="performance-label">Velocity Trend</div>
                <div className="performance-value">
                  <span className="trend-up">â†—ï¸</span>
                  <span>{metrics?.productivity_trends?.velocity_trend || 'Stable'}</span>
                </div>
              </div>

              <div className="performance-item">
                <div className="performance-label">Completion Rate</div>
                <div className="performance-value">
                  <span className="percentage">{metrics?.productivity_trends?.completion_rate || 0}%</span>
                </div>
              </div>

              <div className="performance-item">
                <div className="performance-label">Team Satisfaction</div>
                <div className="performance-value">
                  <span className="rating">{metrics?.productivity_trends?.team_satisfaction || 0}/5.0</span>
                  <div className="stars">â­â­â­â­â­</div>
                </div>
              </div>
            </div>
          </div>

          {/* Integration Status */}
          <div className="dashboard-card integration-status">
            <div className="card-header">
              <div className="card-title">
                <span className="card-icon">ðŸ”—</span>
                <h3>Integration Status</h3>
              </div>
            </div>

            <div className="integration-list">
              <div className="integration-item">
                <div className="integration-info">
                  <span className="integration-icon">ðŸ”·</span>
                  <span className="integration-name">JIRA</span>
                </div>
                <div className={`integration-status ${metrics?.integration_status?.jira ? 'connected' : 'disconnected'}`}>
                  {metrics?.integration_status?.jira ? 'Connected' : 'Disconnected'}
                </div>
              </div>

              <div className="integration-item">
                <div className="integration-info">
                  <span className="integration-icon">ðŸ’¬</span>
                  <span className="integration-name">Slack</span>
                </div>
                <div className={`integration-status ${metrics?.integration_status?.slack ? 'connected' : 'disconnected'}`}>
                  {metrics?.integration_status?.slack ? 'Connected' : 'Disconnected'}
                </div>
              </div>

              <div className="integration-item">
                <div className="integration-info">
                  <span className="integration-icon">ðŸ™</span>
                  <span className="integration-name">GitHub</span>
                </div>
                <div className={`integration-status ${metrics?.integration_status?.github ? 'connected' : 'disconnected'}`}>
                  {metrics?.integration_status?.github ? 'Connected' : 'Disconnected'}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// =======================
// ENHANCED STANDUP MODULE
// =======================

const StandupModule = () => {
  const [teamMembers, setTeamMembers] = useState([]);
  const [selectedMember, setSelectedMember] = useState('');
  const [input, setInput] = useState({
    team_member_id: '',
    yesterday: '',
    today: '',
    blockers: '',
    mood: 'neutral',
    confidence_level: 5
  });
  const [output, setOutput] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recentStandups, setRecentStandups] = useState([]);

  useEffect(() => {
    fetchTeamMembers();
    fetchRecentStandups();
  }, []);

  const fetchTeamMembers = async () => {
    try {
      const response = await axios.get(`${API}/team/members`);
      setTeamMembers(response.data);
    } catch (error) {
      console.error('Error fetching team members:', error);
      // Mock data for demo
      setTeamMembers([
        { id: '1', name: 'Sarah Johnson', role: 'Senior Developer' },
        { id: '2', name: 'Alex Rivera', role: 'Frontend Developer' },
        { id: '3', name: 'Mike Chen', role: 'Backend Developer' },
        { id: '4', name: 'Emily Davis', role: 'QA Engineer' }
      ]);
    }
  };

  const fetchRecentStandups = async () => {
    try {
      const response = await axios.get(`${API}/standup?limit=10`);
      setRecentStandups(response.data);
    } catch (error) {
      console.error('Error fetching standups:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.team_member_id) {
      alert('Please select a team member');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(`${API}/standup`, input);
      setOutput(response.data);
      fetchRecentStandups();
    } catch (error) {
      console.error('Error generating standup:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadExample = () => {
    setInput({
      ...input,
      team_member_id: teamMembers[0]?.id || '1',
      yesterday: "Yesterday I finished the user authentication module and fixed three bugs in the payment system. I also completed the code review for the new API endpoints and helped onboard the new team member.",
      today: "Today I'm working on the dashboard UI improvements, will start the payment integration with Stripe, and plan to attend the architecture review meeting. I'll also finish the unit tests for the auth module.",
      blockers: "I'm waiting for the staging environment credentials from DevOps team, which is blocking my integration testing. Also need clarification on the new design requirements from the product team.",
      mood: 'positive',
      confidence_level: 7
    });
    setSelectedMember(teamMembers[0]?.id || '1');
  };

  return (
    <div className="module-container">
      <TopBar 
        title="ðŸ¤– AI Standup Generator" 
        subtitle="Transform informal updates into structured, professional daily standup reports with AI-powered sentiment analysis and risk assessment."
      />

      <div className="module-content">
        <div className="module-grid">
          {/* Enhanced Input Panel */}
          <div className="input-panel">
            <div className="panel-card">
              <div className="card-header-with-action">
                <div className="card-icon-title">
                  <div className="icon-circle">ðŸ¤–</div>
                  <h3>Generate Standup Report</h3>
                </div>
                <button onClick={loadExample} className="example-btn">
                  Load Example
                </button>
              </div>

              <form onSubmit={handleSubmit} className="standup-form">
                <div className="form-group">
                  <label className="form-label">Team Member *</label>
                  <select 
                    value={selectedMember}
                    onChange={(e) => {
                      setSelectedMember(e.target.value);
                      setInput({...input, team_member_id: e.target.value});
                    }}
                    className="form-select"
                    required
                  >
                    <option value="">Select team member...</option>
                    {teamMembers.map(member => (
                      <option key={member.id} value={member.id}>
                        {member.name} - {member.role}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label className="form-label">What did you work on yesterday? *</label>
                  <textarea
                    value={input.yesterday}
                    onChange={(e) => setInput({...input, yesterday: e.target.value})}
                    className="form-textarea"
                    rows="3"
                    placeholder="Describe your completed tasks, achievements, and progress..."
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">What will you work on today? *</label>
                  <textarea
                    value={input.today}
                    onChange={(e) => setInput({...input, today: e.target.value})}
                    className="form-textarea"
                    rows="3"
                    placeholder="List your planned tasks and objectives for today..."
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Any blockers or challenges?</label>
                  <textarea
                    value={input.blockers}
                    onChange={(e) => setInput({...input, blockers: e.target.value})}
                    className="form-textarea"
                    rows="2"
                    placeholder="Describe any obstacles, dependencies, or support needed..."
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label className="form-label">Mood</label>
                    <select 
                      value={input.mood}
                      onChange={(e) => setInput({...input, mood: e.target.value})}
                      className="form-select"
                    >
                      <option value="very_positive">Very Positive ðŸ˜„</option>
                      <option value="positive">Positive ðŸ™‚</option>
                      <option value="neutral">Neutral ðŸ˜</option>
                      <option value="negative">Concerned ðŸ˜•</option>
                      <option value="very_negative">Stressed ðŸ˜ž</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Confidence Level ({input.confidence_level}/10)</label>
                    <input
                      type="range"
                      min="1"
                      max="10"
                      value={input.confidence_level}
                      onChange={(e) => setInput({...input, confidence_level: parseInt(e.target.value)})}
                      className="form-range"
                    />
                    <div className="range-labels">
                      <span>Low</span>
                      <span>High</span>
                    </div>
                  </div>
                </div>

                <button
                  type="submit"
                  disabled={loading}
                  className="submit-btn primary large"
                >
                  {loading ? <LoadingSpinner text="Generating AI analysis..." /> : (
                    <>
                      <span className="btn-icon">âœ¨</span>
                      Generate AI Standup Analysis
                    </>
                  )}
                </button>
              </form>
            </div>

            {/* Team Standups Overview */}
            <div className="panel-card">
              <div className="card-header-simple">
                <div className="icon-circle small">ðŸ“Š</div>
                <h3>Team Standups Today</h3>
              </div>

              <div className="team-overview">
                {teamMembers.slice(0, 4).map((member, index) => (
                  <div key={member.id} className="team-member-status">
                    <div className="member-avatar">{member.name.charAt(0)}</div>
                    <div className="member-info">
                      <div className="member-name">{member.name}</div>
                      <div className="member-status">
                        {index < 2 ? (
                          <span className="status-completed">âœ… Completed</span>
                        ) : (
                          <span className="status-pending">â³ Pending</span>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Enhanced Output Panel */}
          <div className="output-panel">
            {output ? (
              <div className="panel-card">
                <div className="card-header-with-action">
                  <div className="card-icon-title">
                    <div className="icon-circle success">âœ…</div>
                    <h3>AI Analysis Results</h3>
                  </div>
                  <div className="export-actions">
                    <ExportButton data={output} filename="standup-summary" format="markdown" size="small" />
                    <ExportButton data={output} filename="standup-summary" format="slack" size="small" />
                    <ExportButton data={output} filename="standup-summary" format="jira" size="small" />
                    <ExportButton data={output} filename="standup-summary" format="json" size="small" />
