    } finally {
      setLoading(false);
    }
  };

  const loadExample = () => {
    setInput({
      went_well: "Great collaboration on the payment integration, code reviews were thorough and helpful, team velocity increased significantly, good communication during daily standups",
      went_poorly: "Testing environment was unstable causing delays, unclear requirements led to rework, too many interruptions during focus time",
      improvements: "Set up dedicated testing environment, improve requirement gathering process, establish focus time blocks, better story point estimation",
      team_mood: 'positive'
    });
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
      {/* Input Panel */}
      <div className="space-y-6">
        <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-medium text-gray-900">Sprint Retrospective</h2>
            <button
              onClick={loadExample}
              className="text-sm text-blue-600 hover:text-blue-800 underline"
            >
              Load Example
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                What went well this sprint? *
              </label>
              <textarea
                value={input.went_well}
                onChange={(e) => setInput({...input, went_well: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="List successes, achievements, and positive experiences..."
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                What didn't go well? *
              </label>
              <textarea
                value={input.went_poorly}
                onChange={(e) => setInput({...input, went_poorly: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="Identify challenges, blockers, and areas for improvement..."
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                What could be improved? *
              </label>
              <textarea
                value={input.improvements}
                onChange={(e) => setInput({...input, improvements: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                rows="3"
                placeholder="Suggest specific actions and process improvements..."
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Overall Team Mood
              </label>
              <select
                value={input.team_mood}
                onChange={(e) => setInput({...input, team_mood: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="very_positive">Very Positive 😄</option>
                <option value="positive">Positive 🙂</option>
                <option value="neutral">Neutral 😐</option>
                <option value="negative">Negative 😕</option>
                <option value="very_negative">Very Negative 😞</option>
              </select>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? <LoadingSpinner /> : 'Generate Retrospective Summary'}
            </button>
          </form>
        </div>

        {/* Recent Retrospectives */}
        {recentRetros.length > 0 && (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-md font-medium text-gray-900 mb-4">Recent Retrospectives</h3>
            <div className="space-y-2">
              {recentRetros.map((retro) => (
                <div key={retro.id} className="p-3 bg-gray-50 rounded border text-sm">
                  <div className="font-medium text-gray-800 mb-1">{retro.summary}</div>
                  <div className="text-gray-600 text-xs">
                    {retro.key_insights.length} insights • {retro.action_items.length} actions • {new Date(retro.timestamp).toLocaleDateString()}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Output Panel */}
      <div className="space-y-6">
        {output ? (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-medium text-gray-900">Retrospective Analysis</h2>
              <div className="flex space-x-2">
                <ExportButton data={output} filename="retrospective" format="markdown" />
                <ExportButton data={output} filename="retrospective" format="json" />
              </div>
            </div>

            <div className="space-y-6">
              {/* Mood Analysis */}
              <div className={`p-4 rounded-lg border ${
                output.mood_analysis.includes('positive') ? 'bg-green-50 border-green-200' :
                output.mood_analysis.includes('negative') ? 'bg-red-50 border-red-200' :
                'bg-blue-50 border-blue-200'
              }`}>
                <div className="font-medium text-gray-900 mb-2">🎭 Team Sentiment</div>
                <div className="text-sm text-gray-700">{output.mood_analysis}</div>
              </div>

              {/* Key Insights */}
              <div>
                <h3 className="font-medium text-gray-900 mb-3">💡 Key Insights</h3>
                <ul className="text-sm text-gray-700 space-y-2">
                  {output.key_insights.map((insight, index) => (
                    <li key={index} className="flex items-start">
                      <span className="text-blue-500 mr-2">•</span>
                      {insight}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Action Items */}
              <div>
                <h3 className="font-medium text-gray-900 mb-3">✅ Action Items</h3>
                <div className="space-y-2">
                  {output.action_items.map((item, index) => (
                    <div key={index} className="flex items-start p-3 bg-yellow-50 border border-yellow-200 rounded">
                      <span className="text-yellow-600 mr-2">📌</span>
                      <div className="text-sm text-gray-700">{item}</div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Recommendations */}
              <div>
                <h3 className="font-medium text-gray-900 mb-3">🚀 Recommendations</h3>
                <ul className="text-sm text-gray-700 space-y-2">
                  {output.recommendations.map((rec, index) => (
                    <li key={index} className="flex items-start">
                      <span className="text-green-500 mr-2">•</span>
                      {rec}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Formatted Output */}
              <div>
                <h3 className="font-medium text-gray-900 mb-3">📄 Formatted Summary</h3>
                <div className="text-sm bg-gray-50 p-4 rounded border">
                  <pre className="whitespace-pre-wrap font-sans">{output.formatted_output}</pre>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200 border-dashed">
            <div className="text-center text-gray-500">
              <div className="text-4xl mb-4">🔄</div>
              <h3 className="text-lg font-medium mb-2">Create Sprint Retrospective</h3>
              <p className="text-sm">
                Reflect on your sprint and generate a comprehensive retrospective
                with insights, action items, and team sentiment analysis.
              </p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const Dashboard = () => {
  const [metrics, setMetrics] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMetrics();
    const interval = setInterval(fetchMetrics, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchMetrics = async () => {
    try {
      const response = await axios.get(`${API}/metrics`);
      setMetrics(response.data);
    } catch (error) {
      console.error('Error fetching metrics:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner />
      </div>
    );
  }

  const metricCards = [
    { label: 'Standups Generated', value: metrics?.standups_generated || 0, icon: '📝', color: 'blue' },
    { label: 'Tickets Created', value: metrics?.tickets_created || 0, icon: '🎫', color: 'green' },
    { label: 'Sprints Planned', value: metrics?.sprints_planned || 0, icon: '⚡', color: 'purple' },
    { label: 'Blockers Detected', value: metrics?.blockers_detected || 0, icon: '🚫', color: 'red' },
    { label: 'Retrospectives', value: metrics?.retrospectives_completed || 0, icon: '🔄', color: 'indigo' },
    { label: 'Team Velocity', value: `${metrics?.avg_team_velocity || 0}`, icon: '📈', color: 'yellow' }
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">AI Scrum Master Dashboard</h1>
        <p className="text-gray-600">Monitor your team's agile workflow and productivity metrics</p>
      </div>

      {/* Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {metricCards.map((metric, index) => (
          <div key={index} className="bg-white p-6 rounded-lg shadow-sm border border-gray-200 metric-card">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">{metric.label}</p>
                <p className="text-2xl font-bold text-gray-900">{metric.value}</p>
              </div>
              <div className={`p-3 rounded-full bg-${metric.color}-100`}>
                <span className="text-2xl">{metric.icon}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Quick Actions */}
      <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          <button className="p-4 text-left border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors">
            <div className="text-xl mb-2">📝</div>
            <div className="font-medium text-gray-900">Daily Standup</div>
            <div className="text-sm text-gray-600">Generate formatted standup summary</div>
          </button>

          <button className="p-4 text-left border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors">
            <div className="text-xl mb-2">🎫</div>
            <div className="font-medium text-gray-900">Create Ticket</div>
            <div className="text-sm text-gray-600">Convert idea to structured ticket</div>
          </button>

          <button className="p-4 text-left border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors">
            <div className="text-xl mb-2">⚡</div>
            <div className="font-medium text-gray-900">Plan Sprint</div>
            <div className="text-sm text-gray-600">Break down objectives into tasks</div>
          </button>
        </div>
      </div>

      {/* Team Insights */}
      <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Team Insights</h2>
        <div className="space-y-4">
          <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="flex items-center">
              <span className="text-blue-500 mr-3">💡</span>
              <div>
                <div className="font-medium text-blue-900">Productivity Trend</div>
                <div className="text-sm text-blue-700">Team velocity has increased 15% over the last 3 sprints</div>
              </div>
            </div>
          </div>

          <div className="p-4 bg-green-50 border border-green-200 rounded-lg">
            <div className="flex items-center">
              <span className="text-green-500 mr-3">✅</span>
              <div>
                <div className="font-medium text-green-900">Quality Metrics</div>
                <div className="text-sm text-green-700">87% of tickets completed without major revisions</div>
              </div>
            </div>
          </div>

          <div className="p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
            <div className="flex items-center">
              <span className="text-yellow-500 mr-3">⚠️</span>
              <div>
                <div className="font-medium text-yellow-900">Process Improvement</div>
                <div className="text-sm text-yellow-700">Consider reducing meeting overhead for 8% productivity gain</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// =======================
// MAIN APP COMPONENT
// =======================

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');

  const renderActiveModule = () => {
    switch (activeTab) {
      case 'standup':
        return <StandupModule />;
      case 'ticket':
        return <TicketModule />;
      case 'sprint':
        return <SprintModule />;
      case 'blockers':
        return <BlockersModule />;
      case 'retro':
        return <RetroModule />;
      case 'dashboard':
        return <Dashboard />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="App">
      <Layout activeTab={activeTab} setActiveTab={setActiveTab}>
        {renderActiveModule()}
      </Layout>
    </div>
  );
}

export default App;
