import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

// =======================
// SHARED COMPONENTS
// =======================

const Sidebar = ({ activeTab, setActiveTab }) => {
  const menuItems = [
    { id: 'dashboard', name: 'Dashboard', icon: 'ðŸ“Š', desc: 'Overview & Analytics' },
    { id: 'standup', name: 'Daily Standup', icon: 'ðŸ‘¥', desc: 'AI Update Composer' },
    { id: 'sprint', name: 'Sprint Planning', icon: 'âš¡', desc: 'AI Story Splitter' },
    { id: 'ticket', name: 'Task Generator', icon: 'ðŸ“‹', desc: 'Smart Ticket Builder' },
    { id: 'retro', name: 'Retrospective', icon: 'ðŸ”„', desc: 'Sprint Reflection Tool' },
    { id: 'blockers', name: 'Backlog Groomer', icon: 'ðŸš§', desc: 'Prioritization Assistant' }
  ];

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <div className="logo">
          <div className="logo-icon">AI</div>
          <div className="logo-text">
            <div className="logo-title">AI Scrum Master</div>
            <div className="logo-subtitle">Intelligent Agile Automation</div>
          </div>
        </div>
      </div>

      <nav className="sidebar-nav">
        {menuItems.map((item) => (
          <button
            key={item.id}
            onClick={() => setActiveTab(item.id)}
            className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
          >
            <div className="nav-icon">{item.icon}</div>
            <div className="nav-content">
              <div className="nav-title">{item.name}</div>
              <div className="nav-desc">{item.desc}</div>
            </div>
          </button>
        ))}
      </nav>

      <div className="sidebar-footer">
        <div className="footer-badge">
          <span className="badge-icon">ðŸ¤–</span>
          <span className="badge-text">Local AI-Powered</span>
        </div>
      </div>
    </div>
  );
};

const TopBar = ({ title, subtitle }) => {
  return (
    <div className="topbar">
      <div className="topbar-content">
        <div className="topbar-info">
          <h1 className="topbar-title">{title}</h1>
          <p className="topbar-subtitle">{subtitle}</p>
        </div>
        <div className="topbar-actions">
          <div className="status-indicator">
            <div className="status-dot active"></div>
            <span className="status-text">All AI-Powered</span>
          </div>
        </div>
      </div>
    </div>
  );
};

const ExportButton = ({ data, filename, format = 'json' }) => {
  const handleExport = () => {
    let content;
    let mimeType;

    if (format === 'json') {
      content = JSON.stringify(data, null, 2);
      mimeType = 'application/json';
    } else if (format === 'markdown') {
      content = data.markdown_format || data.formatted_output || JSON.stringify(data, null, 2);
      mimeType = 'text/markdown';
    } else if (format === 'slack') {
      content = data.slack_format || data.formatted_output || JSON.stringify(data, null, 2);
      mimeType = 'text/plain';
    }

    const blob = new Blob([content], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${filename}.${format === 'json' ? 'json' : format === 'markdown' ? 'md' : 'txt'}`;
    document.body.appendChild(a);
    a.click();
    URL.revokeObjectURL(url);
    document.body.removeChild(a);
  };

  const formatColors = {
    json: 'bg-blue-100 text-blue-700 hover:bg-blue-200',
    markdown: 'bg-green-100 text-green-700 hover:bg-green-200',
    slack: 'bg-purple-100 text-purple-700 hover:bg-purple-200'
  };

  return (
    <button
      onClick={handleExport}
      className={`export-btn ${formatColors[format]}`}
    >
      <span className="export-icon">ðŸ“¥</span>
      <span>{format.toUpperCase()}</span>
    </button>
  );
};

const LoadingSpinner = () => (
  <div className="loading-spinner">
    <div className="spinner"></div>
    <span>Generating...</span>
  </div>
);

// =======================
// DASHBOARD COMPONENT
// =======================

const Dashboard = () => {
  const [metrics, setMetrics] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMetrics();
    const interval = setInterval(fetchMetrics, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchMetrics = async () => {
    try {
      const response = await axios.get(`${API}/metrics`);
      setMetrics(response.data);
    } catch (error) {
      console.error('Error fetching metrics:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <LoadingSpinner />
      </div>
    );
  }

  const summaryCards = [
    {
      title: 'Tasks This Week',
      value: '5',
      change: '+12% from last week',
      icon: 'ðŸ“',
      color: 'blue'
    },
    {
      title: 'Completed Tasks',
      value: '1',
      change: '+20% completion rate',
      icon: 'âœ…',
      color: 'green'
    },
    {
      title: 'Standup Reports',
      value: metrics?.standups_generated || '0',
      change: 'AI-generated this week',
      icon: 'ðŸ‘¥',
      color: 'purple'
    },
    {
      title: 'Active Sprints',
      value: '1',
      change: 'Sprint 24 in progress',
      icon: 'âš¡',
      color: 'orange'
    }
  ];

  return (
    <div className="dashboard">
      <TopBar 
        title="AI Scrum Master Dashboard" 
        subtitle="Your intelligent Agile automation overview for August 1, 2025"
      />

      <div className="dashboard-content">
        {/* Summary Cards */}
        <div className="summary-grid">
          {summaryCards.map((card, index) => (
            <div key={index} className={`summary-card ${card.color}`}>
              <div className="card-header">
                <div className="card-icon">{card.icon}</div>
                <div className="card-stats">
                  <div className="card-value">{card.value}</div>
                  <div className="card-change">{card.change}</div>
                </div>
              </div>
              <div className="card-title">{card.title}</div>
            </div>
          ))}
        </div>

        {/* Main Content Grid */}
        <div className="dashboard-grid">
          {/* Active Sprint */}
          <div className="dashboard-card sprint-card">
            <div className="card-header-section">
              <div className="card-icon-large">ðŸŽ¯</div>
              <div>
                <h3 className="card-title-large">Sprint 24</h3>
                <p className="card-subtitle">Complete user authentication system and payment bug fixes</p>
              </div>
              <div className="status-badge active">active</div>
            </div>

            <div className="sprint-progress">
              <div className="progress-header">
                <span className="progress-label">Sprint Progress</span>
                <span className="progress-value">0%</span>
              </div>
              <div className="progress-bar">
                <div className="progress-fill" style={{width: '0%'}}></div>
              </div>
              <div className="progress-details">
                <span>0 of 4 tasks completed</span>
                <span>-537 days remaining</span>
              </div>
            </div>

            <div className="task-distribution">
              <div className="distribution-item completed">
                <div className="distribution-count">0</div>
                <div className="distribution-label">Completed</div>
              </div>
              <div className="distribution-item progress">
                <div className="distribution-count">2</div>
                <div className="distribution-label">In Progress</div>
              </div>
              <div className="distribution-item blocked">
                <div className="distribution-count">1</div>
                <div className="distribution-label">Blocked</div>
              </div>
              <div className="distribution-item story">
                <div className="distribution-count">0</div>
                <div className="distribution-label">Story Points</div>
              </div>
            </div>

            <div className="sprint-meta">
              <div className="meta-item">
                <span className="meta-icon">ðŸ“…</span>
                <span>Jan 7 - Jan 21</span>
              </div>
              <div className="meta-item">
                <span className="meta-icon">âš¡</span>
                <span>Velocity: 28</span>
              </div>
            </div>
          </div>

          {/* Recent Activity */}
          <div className="dashboard-card activity-card">
            <div className="card-header-section">
              <div className="card-icon-large">ðŸ•’</div>
              <h3 className="card-title-large">Recent Activity</h3>
            </div>

            <div className="activity-list">
              {[
                { user: 'Rohit', action: 'Standup by Rohit', status: 'AI Generated', time: 'in about 4 hours', icon: 'ðŸ‘¥', color: 'purple' },
                { user: 'Alan', action: 'Standup by Alan', status: 'AI Generated', time: 'in about 4 hours', icon: 'ðŸ‘¥', color: 'purple' },
                { user: 'Sarah Johnson', action: 'Standup by Sarah Johnson', status: 'AI Generated', time: 'in about 3 hours', icon: 'ðŸ‘¥', color: 'purple' },
                { user: 'Alex Rivera', action: 'Standup by Alex Rivera', status: 'AI Generated', time: 'in about 3 hours', icon: 'ðŸ‘¥', color: 'purple' },
                { user: 'System', action: 'Fix payment processing bug', status: 'blocked', priority: 'urgent', time: 'in about 3 hours', icon: 'ðŸ›', color: 'red' },
                { user: 'System', action: 'Write API documentation', status: 'done', priority: 'medium', time: 'in about 3 hours', icon: 'ðŸ“š', color: 'green' },
                { user: 'System', action: 'Set up CI/CD pipeline', status: 'in progress', priority: 'high', time: 'in about 3 hours', icon: 'ðŸ”§', color: 'blue' }
              ].map((activity, index) => (
                <div key={index} className="activity-item">
                  <div className="activity-icon">{activity.icon}</div>
                  <div className="activity-content">
                    <div className="activity-main">
                      <span className="activity-user">{activity.user}</span>
                      <span className="activity-action">{activity.action}</span>
                    </div>
                    <div className="activity-meta">
                      <span className={`activity-status ${activity.status?.replace(' ', '-').toLowerCase()}`}>
                        {activity.status}
                      </span>
                      {activity.priority && (
                        <span className={`activity-priority ${activity.priority}`}>
                          {activity.priority}
                        </span>
                      )}
                      <span className="activity-time">{activity.time}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Task Distribution Chart */}
        <div className="dashboard-card chart-card">
          <div className="card-header-section">
            <div className="card-icon-large">ðŸ“Š</div>
            <h3 className="card-title-large">Task Distribution</h3>
          </div>
          <div className="chart-container">
            <div className="donut-chart">
              <svg viewBox="0 0 200 200" className="donut-svg">
                <circle cx="100" cy="100" r="80" fill="none" stroke="#e5e5e5" strokeWidth="20"/>
                <circle cx="100" cy="100" r="80" fill="none" stroke="#3b82f6" strokeWidth="20" 
                        strokeDasharray="251.2" strokeDashoffset="188.4" transform="rotate(-90 100 100)"/>
                <circle cx="100" cy="100" r="80" fill="none" stroke="#10b981" strokeWidth="20" 
                        strokeDasharray="251.2" strokeDashoffset="125.6" transform="rotate(90 100 100)"/>
                <circle cx="100" cy="100" r="80" fill="none" stroke="#ef4444" strokeWidth="20" 
                        strokeDasharray="251.2" strokeDashoffset="188.4" transform="rotate(180 100 100)"/>
              </svg>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// =======================
// STANDUP MODULE
// =======================

const StandupModule = () => {
  const [input, setInput] = useState({
    yesterday: '',
    today: '',
    blockers: ''
  });
  const [output, setOutput] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recentStandups, setRecentStandups] = useState([]);

  useEffect(() => {
    fetchRecentStandups();
  }, []);

  const fetchRecentStandups = async () => {
    try {
      const response = await axios.get(`${API}/standup?limit=5`);
      setRecentStandups(response.data);
    } catch (error) {
      console.error('Error fetching standups:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post(`${API}/standup`, input);
      setOutput(response.data);
      fetchRecentStandups();
    } catch (error) {
      console.error('Error generating standup:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadExample = () => {
    setInput({
      yesterday: "Yesterday I finished the user authentication module and fixed three bugs in the payment system. Today I'm working on the dashboard UI and will start the API integration. I'm blocked on getting the staging environment credentials from DevOps.",
      today: "Working on payment integration, user dashboard improvements, and sprint planning",
      blockers: "Waiting for API keys from payment provider, potential delay of 1-2 days"
    });
  };

  return (
    <div className="module-container">
      <TopBar 
        title="ðŸ¤– AI Standup Generator" 
        subtitle="Transform your informal updates into structured, professional daily standup reports. Just describe what you've been working on naturally, and let AI format it perfectly."
      />

      <div className="module-content">
        <div className="module-grid">
          {/* Left Panel - Input */}
          <div className="input-panel">
            <div className="panel-card">
              <div className="card-header-with-action">
                <div className="card-icon-title">
                  <div className="icon-circle">ðŸ¤–</div>
                  <h3>Generate Standup Report</h3>
                </div>
                <button onClick={loadExample} className="example-btn">
                  Try Example
                </button>
              </div>

              <form onSubmit={handleSubmit} className="standup-form">
                <div className="form-group">
                  <label className="form-label">Team Member Name</label>
                  <input 
                    type="text"
                    className="form-input"
                    placeholder="e.g., Sarah Johnson"
                    defaultValue="Sarah Johnson"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Natural Language Update</label>
                  <textarea
                    value={input.yesterday + ' ' + input.today + ' ' + input.blockers}
                    onChange={(e) => {
                      const text = e.target.value;
                      setInput({
                        yesterday: text.substring(0, text.length / 3),
                        today: text.substring(text.length / 3, 2 * text.length / 3),
                        blockers: text.substring(2 * text.length / 3)
                      });
                    }}
                    className="form-textarea large"
                    rows="8"
                    placeholder="e.g., Yesterday I finished the user authentication module and fixed three bugs in the payment system. Today I'm working on the dashboard UI and will start the API integration. I'm blocked on getting the staging environment credentials from DevOps."
                    required
                  />
                  <div className="form-help">
                    Describe your work naturally. Mention what you did yesterday, what you're doing today, and any blockers.
                  </div>
                </div>

                <button
                  type="submit"
                  disabled={loading}
                  className="submit-btn primary large"
                >
                  {loading ? <LoadingSpinner /> : (
                    <>
                      <span className="btn-icon">âœ¨</span>
                      Generate Standup
                    </>
                  )}
                </button>
              </form>
            </div>

            {/* Recent Standups */}
            <div className="panel-card">
              <div className="card-header-simple">
                <div className="icon-circle small">ðŸ•’</div>
                <h3>Recent Standups</h3>
              </div>

              <div className="recent-list">
                {[
                  { user: 'Rohit', tasks: '1 tasks completed', planned: '1 tasks planned', blockers: '1 blockers reported', time: 'in about 4 hours' },
                  { user: 'Alan', tasks: '1 tasks completed', planned: '0 tasks planned', blockers: '1 blockers reported', time: 'in about 4 hours' },
                  { user: 'Sarah Johnson', tasks: '3 tasks completed', planned: '3 tasks planned', time: 'in about 5 hours' },
                  { user: 'Alex Rivera', tasks: '2 tasks completed', planned: '1 tasks planned', blockers: '1 blockers reported', time: 'in about 5 hours' }
                ].map((standup, index) => (
                  <div key={index} className="recent-item">
                    <div className="recent-header">
                      <div className="recent-icon">ðŸ‘¤</div>
                      <div className="recent-info">
                        <div className="recent-user">{standup.user}</div>
