                    <pre className="formatted-output">
                      {output.formatted_output}
                    </pre>
                  </div>
                </div>
              </div>
            ) : (
              <div className="panel-card empty-state">
                <div className="empty-state-content">
                  <div className="empty-icon">ðŸ“</div>
                  <h3>Generate Your First Standup</h3>
                  <p>Fill out the form on the left to generate a professional standup summary. The AI will create formatted output suitable for Slack, Jira, or documentation.</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

